package com.bridgelabz.userservice.dto;

public class LoginDTO {

	private String mailOrMobile;
	private String password;

	public LoginDTO() {
		// TODO Auto-generated constructor stub
	}

	public LoginDTO(String mailOrMobile, String password) {
		super();
		this.mailOrMobile = mailOrMobile;
		this.password = password;
	}

	public String getMailOrMobile() {
		return mailOrMobile;
	}

	public void setMailOrMobile(String mailOrMobile) {
		this.mailOrMobile = mailOrMobile;
	}

	public String getPassword() {
		return password;
	}

	public void setPassword(String password) {
		this.password = password;
	}

}
